import React,  { useState } from 'react';

export default function Form(){
    const [file, setFile] = useState(null);
    const [fileName, setFileName] = useState('📁 Choose an Image');

    const handleChange = (event) => {
        const selectedFile = event.target.files[0];
        setFile(selectedFile);
        setFileName(selectedFile ? selectedFile.name : '📁 Choose an Image');
    }

    const handleSubmit = (event) => {

        event.preventDefault();
        if (!file) {
            alert('Please select a file before submitting.');
            return;
        }

        const formData = new FormData();
        formData.append('image', file);

        fetch('http://localhost:5000/upload', {
            method: 'POST',
            body: formData,
        })
        .then(response => response.json())
        .then(data => {
            console.log(data);
            if (data.success) {
                alert('Background removed successfully!');
                const downloadLink = document.createElement('a');
                downloadLink.href = data.download_link; // Assuming the server returns a URL for the processed image
                downloadLink.download = 'processed_image.png'; // You can set a default name for the downloaded file
                downloadLink.click();
            } else {
                alert('Error removing background: ' + data.message);
            }
        })
        .catch(error => {
            console.error('Error:', error);
        });
    }

    return(
        <div className="file-upload-container">

                <label htmlFor="file-input" className="file-upload-label">
                    {fileName}
                </label>
                <input type="file" onChange={handleChange} id="file-input" accept="image/*" required/><br/>
                <br/>
                <button className="upload-button" onClick={handleSubmit}>Remove Image Background</button>
        </div>
    )

}
