        setFileName(selectedFile ? selectedFile.name : 'üìÅ Choose an Image');
    }

    return(
        <div class="file-upload-container">
            <form method="POST" action="http://localhost:5000/upload" encType="multipart/form-data">
                <label for="file-input" class="file-upload-label">
                    {fileName}
                </label>
                <input type="file" onChange={handleChange} id="file-input" accept="image/*" required/><br/>
                <br/>
                <button className="upload-button">Upload Image</button>
            </form>
        </div>

    )

}
